import React, { useState, useEffect } from "react";
import "../styles/UserDashboard.css";
import { FaUserCircle } from "react-icons/fa";
import hostelImage from "../assets/hostelroom.jpg";

function UserDashboard() {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  useEffect(() => {
    console.log(FaUserCircle);
  }, []);

  const bookings = [
    {
      name: "Aiswarya Hostel",
      address: "University Road, Kochi, Kerala 682022",
      review: 4.5,
      amountPaid: "₹8000",
      status: "Confirmed",
    },
  ];

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="header">
        <div className="logo">BOOKmyHOSTEL</div>
        <nav className="nav-links">
          <a href="/">Home</a>
          <a href="/search">Search Hostels</a>
          <a href="/reviews">Reviews</a>
          <a href="/contact">Contact Us</a>
        </nav>
        <div className="user-dropdown">
          <FaUserCircle className="user-icon" onClick={toggleDropdown} />
          {dropdownOpen && (
            <div className="dropdown-menu">
              <a href="/profile">Profile</a>
              <a href="/edit-profile">Edit Profile</a>
              <a href="/saved-hostels">Saved Hostels</a>
              <a href="/logout">Log Out</a>
            </div>
          )}
        </div>
      </header>

      {/* Hero Section with Background Image */}
      <section className="hero">
        <div className="hero-section">
          <img src={hostelImage} alt="Hostel Room" />
          <h1>Welcome, User!</h1>
          <div className="search-bar">
            <input type="text" placeholder="Search hostels..." />
            <button>Search</button>
          </div>
        </div>
      </section>

      {/* Recently Booked Hostels */}
      <section className="recent-bookings">
        <h2>Your Recent Bookings</h2>
        <div className="card-container">
          {bookings.map((booking, index) => (
            <div key={index} className="card">
              <div className="card-details">
                <h3>{booking.name}</h3>
                <p>{booking.address}</p>
                <p>Review: ⭐{booking.review}</p>
                <p>Fees Paid: {booking.amountPaid}</p>
                <p className="confirmed">{booking.status}</p>
                <button className="view-details">View Details</button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default UserDashboard;
