import React, { useState } from "react";
import "../styles/UserDashboard.css";
import { FaUserCircle } from "react-icons/fa";

function UserDashboard() {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const bookings = [
    {
      name: "Aiswarya Hostel",
      address: "University Road, Kochi, Kerala 682022",
      review: 4.5,
      amountPaid: "₹8000",
      status: "Confirmed",
    },
  ];

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="header">
        <div className="logo">BOOKmyHOSTEL</div>
        <nav className="nav-links">
          <a href="/">Home</a>
          <a href="/search">Search Hostels</a>
          <a href="/reviews">Reviews</a>
          <a href="/contact">Contact Us</a>
        </nav>
        <div className="user-dropdown">
          <FaUserCircle className="user-icon" onClick={toggleDropdown} />
          {dropdownOpen && (
            <div className="dropdown-menu">
              <a href="/profile">Profile</a>
              <a href="/edit-profile">Edit Profile</a>
              <a href="/saved-hostels">Saved Hostels</a>
              <a href="/logout">Log Out</a>
            </div>
          )}
        </div>
      </header>

      {/* Hero Section with Search */}
      <section className="hero">
      <img src="/hostelroom.jpg" alt="Hostel Room" className="hero-image" />
      <div className="hero-content">
        <h1>Welcome, User!</h1>
        <div className="search-bar">
          <input type="text" placeholder="Search hostels..." />
          <button>Search</button>
        </div>
      </div>  
      </section>

  {/* Recently Booked Hostels */}
<section className="recent-bookings">
  <h2>Your Recent Bookings</h2>
  <div className="card-container">
    {Array.isArray(bookings) && bookings.length > 0 ? (
      bookings.map((booking, index) => (
        <div key={index} className="card">
          <div className="card-details">
            <h3>{booking.name || "Aiswarya hostel"}</h3>
            <p>{booking.address || "Kalamassery, Kochi"}</p>
            <p>Fees Paid: {booking.amountPaid !== undefined ? `$${booking.amountPaid}` : "Not specified"}</p>
          </div>
        </div>
      ))
    ) : (
      <p>No bookings available.</p>
    )}
  </div>
</section>


    </div>
  );
}

export default UserDashboard;
