import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../styles/OwnerLogin.css"; // Make sure the correct path is used

const OwnerLoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch("http://localhost:5000/api/owner/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.message);
        return;
      }

      // Store token in local storage
      localStorage.setItem("ownerToken", data.token);
      localStorage.setItem("ownerId", data.ownerId);

      // Redirect to Owner Dashboard
      navigate("/owner-dashboard");
    } catch (error) {
      setError("Login failed. Try again.");
    }
  };

  return (
    <div className="owner-login-container">
      <div className="login-form">
        <h2>Owner Login</h2>
       
        <form onSubmit={handleLogin}>
          <label>Email</label>
          <input type="email" placeholder="Enter your email" placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required />
          <label>Password</label>
          <input type="password" placeholder="Enter your password" value={password}
            onChange={(e) => setPassword(e.target.value)}
            required/>
          <button type="submit">Login</button>
        </form>
        <a href="/forgot-password" className="forgot-password">
          Forgot Password?
        </a>
        <div className="signup-link">
          <p>Don't have an account?</p>
          <Link to="/owner-signup">Sign Up</Link>
        </div>
      </div>
    </div>
  );
};

export default OwnerLoginPage;
