import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../styles/OwnerLogin.css"; // Ensure correct path
import axios from "axios";

const OwnerLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent default form submission
    setError("");
    setLoading(true);
    const trimmedEmail = email.trim();
    const trimmedPassword = password.trim();
    console.log("Sending Data:", { email: trimmedEmail, password: trimmedPassword });
    
    try {
      const response = await fetch("http://localhost:5000/api/owners/login", {
        method:'POST',
      
      //email: trimmedEmail,
      //password: trimmedPassword,
      headers: {
        "Content-Type": "application/json",
      },

      body: JSON.stringify({
        email: trimmedEmail,
        password: trimmedPassword
    }),
    });
    /*{
      headers: {
        "Content-Type": "application/json",
      },
    }*/
     
     
    //Nconsole.log("Login successful:", response);
    //NsetError("");


        /*method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: trimmedEmail, password: trimmedPassword }),
      });*/

      const responseData = await response.json();
      //Nconst data = response;
      console.log("üîπ Response Data:", responseData);

      /*Nif (!response.ok) {
        setError(data.message || "Login failed. Please try again.");
        return;
      }*/
        if (!response.ok) {
          //const errorData = await response.json();  // Parse the response to JSON
          //console.log("Error Response Data:", errorData);
          console.error("‚ùå Server Error:", responseData.message);
          setError(responseData.message || "Login failed. Please try again.");
          return;
        }

        //const data = await response.json();  // Ensure the response is parsed as JSON
        //console.log("üîπ Response Data:", data);

      if (!responseData.token) {
        setError("Login successful, but no token received.");
        return;
      }
      



      // Store token and owner ID in local storage
      localStorage.setItem("ownerToken", responseData.token);
      localStorage.setItem("ownerId", responseData.ownerId);

      console.log("‚úÖ Login Successful! Redirecting...");

      // Send verification email
      await axios.post("http://localhost:5000/api/owners/send-verification", {
        email: trimmedEmail,
      });

      console.log("üìß Verification email sent!");

      // Redirect to Owner Dashboard
      navigate("/owner-dashboard");

    } catch (error) {
      console.error("‚ùå Error:", error);
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div className="owner-login-container">
      <div className="login-form">
        <h2>Owner Login</h2>

        {error && <p className="error-message">{error}</p>} {/* Display error messages */}

        <form onSubmit={handleLogin}>
          <label>Email</label>
          <input 
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />

          <label>Password</label>
          <input 
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit">Login</button>
        </form>

        <a href="/forgot-password" className="forgot-password">
          Forgot Password?
        </a>

        <div className="signup-link">
          <p>Don't have an account?</p>
          <Link to="/owner-signup">Sign Up</Link>
        </div>
      </div>
    </div>
  );
};

export default OwnerLogin;
