import React, { useState, useEffect, useRef } from "react";
import { FaUserCircle } from "react-icons/fa";
import "../styles/UserDashboard.css";

const UserDashboard = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const recentlyBookedHostels = [
    {
      id: 1,
      name: "Sunrise Hostel",
      address: "123 Main Street, City",
      review: "4.5/5",
      amountPaid: "$250",
      confirmed: "Booking Confirmed",
      image: "/public/image.jpg",  // ✅ Fixed path
    },
  ];

  return (
    <div className="user-dashboard">
      {/* Header */}
      <header className="header">
        <div className="logo">BookMyHostel</div>
        <nav className="nav-links">
          <a href="/">Home</a>
          <a href="/search">Search hostels</a>
          <a href="/bookings">Bookings</a>
        </nav>
        <div className="user-icon" onClick={toggleDropdown}>
          <FaUserCircle size={30} />
          <div ref={dropdownRef} className={`dropdown ${dropdownOpen ? "open" : ""}`}>
            <a href="/profile">Profile</a>
            <a href="/edit-profile">Edit Profile</a>
            <a href="/saved-hostels">Saved Hostels</a>
            <a href="/logout" className="logout">Log Out</a>
          </div>
        </div>
      </header>

      {/* Hero Section */}
      <div className="hero-section" style={{ backgroundImage: `url('/public/hostelroom.jpg')` }}>
        <h1>Welcome User</h1>
        <input type="text" placeholder="Search hostels..." className="search-bar" />
      </div>

      {/* Recently Booked Hostels */}
      <div className="card-container">
        <h2>Recently Booked Hostels</h2>
        <div className="hostel-list">
          {recentlyBookedHostels.map((hostel) => (
            <div key={hostel.id} className="card">
              <img src={hostel.image} alt={hostel.name} />
              <div className="card-content">
                <h3 className="card-title">{hostel.name}</h3>
                <p>{hostel.address}</p>
                <p>Review: {hostel.review}</p>
                <p>Amount Paid: {hostel.amountPaid}</p>
                <p className="confirmed">{hostel.confirmed}</p>
                <button className="card-button">View Details</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
