import React, { useState } from "react"; 
import { FaUserCircle } from "react-icons/fa";
import "../styles/UserDashboard.css";

const UserDashboard = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  // Define the links to be rendered in the dropdown
  const dropdownLinks = [
    { text: 'Profile', href: '/profile' },
    { text: 'Edit Profile', href: '/edit-profile' },
    { text: 'Saved Hostels', href: '/saved-hostels' },
    { text: 'Log Out', href: '/logout' }
  ];

  return (
    <div className="dashboard">
      {/* Header Section */}
      <header className="header">
        <div className="logo">BOOKmyHOSTEL</div>
        <nav className="nav-links">
          <a href="/search">Search</a>
          <a href="/reviews">Reviews</a>
          <a href="/contact">Contact</a>
        </nav>
        <div className="user-dropdown">
          <FaUserCircle className="user-icon" onClick={toggleDropdown} />
          {dropdownOpen && (
            <div className="dropdown-menu">
              {/* Map through the dropdownLinks array to render each link */}
              {dropdownLinks.map((link) => (
                <a key={link.href} href={link.href}>
                  {link.text}
                </a>
              ))}
            </div>
          )}
        </div>
      </header>

      {/* Hero Section without Background Image */}
      <section className="hero">
        <div className="overlay">
          <h1>Welcome, User!</h1>
          <div className="search-bar">
            <input type="text" placeholder="Search hostels..." />
            <button>Search</button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default UserDashboard;
