import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../styles/OwnerLogin.css"; // Ensure correct path
import axios from "axios";

const OwnerLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent default form submission
    if (!email || !password) {
      setErrorMessage("Please fill in all fields");
      return;
    }
    /*const trimmedEmail = email.trim();
    const trimmedPassword = password.trim();
    console.log("Sending Data:", { email: trimmedEmail, password: trimmedPassword });*/
    

    try {
      const response = await axios.post("http://localhost:5000/api/owners/login", {
        email,
        password,
    });
    

        /*method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: trimmedEmail, password: trimmedPassword }),
      });*/

      //const data = await response.json();
      console.log("üîπ Response Data:", response.data);

      /*if (!response.ok) {
        setError(data.message || "Login failed. Please try again.");
        return;
      }

      if (!data.token) {
        setError("Login successful, but no token received.");
        return;
      }*/

        if (response.status !== 200) {
          setError(response.data.message || "Login failed. Please try again.");
          return;
        }
        
        if (!response.data.token) {
          setError("Login successful, but no token received.");
          return;
        }



      // Store token and owner ID in local storage
      localStorage.setItem("ownerToken", data.token);
      localStorage.setItem("ownerId", data.ownerId);

      // Redirect to Owner Dashboard
      navigate("/owner-dashboard");

    } catch (error) {
      console.error("‚ùå Error:", error);
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div className="owner-login-container">
      <div className="login-form">
        <h2>Owner Login</h2>

        {error && <p className="error-message">{error}</p>} {/* Display error messages */}

        <form onSubmit={handleLogin}>
          <label>Email</label>
          <input 
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />

          <label>Password</label>
          <input 
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit">Login</button>
        </form>

        <a href="/forgot-password" className="forgot-password">
          Forgot Password?
        </a>

        <div className="signup-link">
          <p>Don't have an account?</p>
          <Link to="/owner-signup">Sign Up</Link>
        </div>
      </div>
    </div>
  );
};

export default OwnerLogin;
