import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/UserDashboard.css"; // For styles

function UserDashboard() {
  const [bookings, setBookings] = useState([]);
  const [savedHostels, setSavedHostels] = useState([]);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    // Replace with your API call
    axios
      .get("http://localhost:5000/api/user/dashboard")
      .then((response) => {
        setBookings(response.data.bookings);
        setSavedHostels(response.data.savedHostels);
        setNotifications(response.data.notifications);
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="dashboard">
      <div className="welcome-section">
        <h1>Welcome back, User!</h1>
        <button className="search-button">Search Hostels</button>
      </div>

      <section className="recent-bookings">
        <h2>Your Recent Bookings</h2>
        <div className="card-container">
          {bookings.map((booking) => (
            <div key={booking._id} className="card">
              <h3>{booking.hostelName}</h3>
              <p>Check-in: {booking.checkInDate}</p>
              <p>Status: {booking.status}</p>
              <button>View Details</button>
              <button>Contact Hostel</button>
            </div>
          ))}
        </div>
      </section>

      <section className="saved-hostels">
        <h2>Saved Hostels</h2>
        <div className="card-container">
          {savedHostels.map((hostel) => (
            <div key={hostel._id} className="card">
              <h3>{hostel.name}</h3>
              <p>Rent: â‚¹{hostel.rent}/month</p>
              <button>Book Now</button>
              <button>Remove</button>
            </div>
          ))}
        </div>
      </section>

      <section className="notifications">
        <h2>Notifications</h2>
        <ul>
          {notifications.map((note, index) => (
            <li key={index}>{note}</li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default UserDashboard;
