import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AddHostel = () => {
  const [hostelDetails, setHostelDetails] = useState({
    name: "",
    location: "",
    amenities: "",
    images: [],
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setHostelDetails((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    setHostelDetails((prevState) => ({
      ...prevState,
      images: [...prevState.images, ...e.target.files],
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the hostel data to the backend via an API call
    // For now, we'll log it to the console and navigate back to the dashboard
    console.log("Hostel Details:", hostelDetails);

    // Redirect to Owner Dashboard after adding the hostel
    navigate("/owner-dashboard");
  };

  return (
    <div className="add-hostel-form">
      <h2>Add New Hostel</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name of Hostel</label>
          <input
            type="text"
            name="name"
            value={hostelDetails.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Location</label>
          <input
            type="text"
            name="location"
            value={hostelDetails.location}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Amenities</label>
          <textarea
            name="amenities"
            value={hostelDetails.amenities}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div>
          <label>Hostel Images</label>
          <input
            type="file"
            name="images"
            multiple
            onChange={handleFileChange}
            required
          />
        </div>
        <button type="submit">Add Hostel</button>
      </form>
    </div>
  );
};

export default AddHostel;
