import React, { useState, useEffect, useRef } from "react";
import { FaUserCircle } from "react-icons/fa";
import "../styles/UserDashboard.css";

const UserDashboard = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const recentlyBookedHostels = [
    {
      id: 1,
      name: "Aiswarya Hostel",
      address: "University Road, South Kalamassery, Kochi, Kerala 682022",
      checkinDate: "20 Jan 2025",
      feesPaid: "‚Çπ8000",
      bookingStatus: "Confirmed",
      image: `${process.env.PUBLIC_URL}/hostel1.jpg`,
    },
  ];

  const savedHostels = [
    {
      id: 2,
      name: "Aiswarya Hostel",
      address: "University Road, South Kalamassery, Kochi, Kerala 682022",
      rating: "4.1",
      rent: "‚Çπ3000/month",
      image: `${process.env.PUBLIC_URL}/hostel1.jpg`,
    },
    {
      id: 3,
      name: "Tag Residency",
      address: "PKA Nagar, Kochi, Kerala 682022",
      rating: "3.9",
      rent: "‚Çπ5500/month",
      image: `${process.env.PUBLIC_URL}/hostel2.jpg`,
    },
  ];

  return (
    <div className="user-dashboard">
      <header className="header">
        <div className="logo">BOOKmyHOSTEL</div>
        <nav className="nav-links">
          <a href="/">Home</a>
          <a href="/search">Search Hostels</a>
          <a href="/reviews">Reviews</a>
          <a href="/contact">Contact Us</a>
        </nav>
        <div className="user-icon" onClick={toggleDropdown} ref={dropdownRef}>
          <FaUserCircle size={30} />
          {dropdownOpen && (
            <div className="dropdown">
              <a href="/profile">Profile</a>
              <a href="/settings">Settings</a>
              <a href="/logout">Log Out</a>
            </div>
          )}
        </div>
      </header>

      <div className="hero-section" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/hostelroom.jpg)` }}>
        <h1>Welcome back, Krishnendhu!</h1>
        <input type="text" placeholder="Search hostels" className="search-bar" />
      </div>

      <div className="content">
        <section className="recent-bookings">
          <h2>Your recent bookings</h2>
          {recentlyBookedHostels.map((hostel) => (
            <div key={hostel.id} className="card">
              <img src={hostel.image} alt={hostel.name} />
              <div className="card-content">
                <h3>{hostel.name}</h3>
                <p>{hostel.address}</p>
                <p>Check-in date: <b>{hostel.checkinDate}</b></p>
                <p>Fees Paid: <b>{hostel.feesPaid}</b></p>
                <p className="confirmed">Booking status: {hostel.bookingStatus}</p>
                <button className="card-button">View details</button>
                <button className="contact-button">Contact hostel</button>
              </div>
            </div>
          ))}
        </section>

        <section className="saved-hostels">
          <h2>Saved Hostels</h2>
          <div className="saved-list">
            {savedHostels.map((hostel) => (
              <div key={hostel.id} className="saved-card">
                <img src={hostel.image} alt={hostel.name} />
                <div className="card-content">
                  <h3>{hostel.name} <span>‚ù§Ô∏è</span></h3>
                  <p>{hostel.address}</p>
                  <p className="rating">{hostel.rating} ‚≠ê</p>
                  <p className="rent">Rent: <b>{hostel.rent}</b></p>
                  <button className="book-now">Book now</button>
                  <button className="remove">Remove</button>
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>

      <div className="notifications">
        <p>üîî Your booking at Aiswarya Hostel has been confirmed!</p>
        <p>‚ö†Ô∏è Only 2 rooms left at Sunrise Hostel. Book now!</p>
        <button className="view-booking">View booking details</button>
      </div>

      <button className="chat-now">Chat now! üí¨</button>
    </div>
  );
};

export default UserDashboard;
